dt-example.css:
/* Ensure that the demo table scrolls */
th, td { white-space: nowrap; }
div.dataTables_wrapper {
	margin: 0 auto;
}

div.container {
	width: 80%;
}

dt-example.js:
$(document).ready(function() {
	var table = $('#example').DataTable( {
		scrollY:        "300px",
		scrollX:        true,
		scrollCollapse: true,
		paging:         false,
		columnDefs: [
			{ width: '20%', targets: 0 }
		],
		fixedColumns: true
	} );
} );
