dt-example.css:
/* Ensure that the demo table scrolls */
th, td { white-space: nowrap; }
div.dataTables_wrapper {
	width: 800px;
	margin: 0 auto;
}

div.ColVis {
	float: left;
}

dt-example.js:
$(document).ready(function() {
	var table = $('#example').DataTable( {
		dom:            "Bfrtip",
		scrollY:        "300px",
		scrollX:        true,
		scrollCollapse: true,
		paging:         false,
		buttons:        [ 'colvis' ],
		fixedColumns:   {
			leftColumns: 2
		}
	} );
} );
