dt-example.css:
/* For this example, force x-scrolling by making the table width smaller */
div.dataTables_wrapper {
	width: 75%;
	margin: 0 auto;
}

dt-example.js:
$(document).ready(function() {
	var table = $('#example').DataTable( {
		ajax:           "../data/2500.txt",
		deferRender:    true,
		scrollY:        200,
		scrollX:        true,
		scrollCollapse: true,
		scroller:       true
	} );

	new $.fn.dataTable.FixedColumns( table );
} );
